            console.log('unmount')
            console.log({fakeAuth})
            reRender()

        }

    }, [fakeAuth]);


    return (
        <Route {...rest} render={({ location }) => {
            return fakeAuth.isAuthenticated === true
                ? children
                : <Redirect to={{
                    pathname: '/login',
                    state: { from: location }
                }} />
        }} />
    )

}



export default PrivateRoute;
